/*
** Cookie module
**/ exports.parse = function(req) {
        var cookies = {};
       
        if (typeof req.headers.cookie !== 'undefined') {
            var cookieList = req.headers.cookie.split(';');
           
            for (var cookie in cookieList) {
                var tab = cookieList[cookie].split('=');
                cookies[tab[0].trim()] = tab[1];
            }
        }
       
        return cookies;
    };


exports.create = function(res,name,value,lifeTime){

    //lifeTime est en min
    var dateExpire = new Date();
     dateExpire.setTime(dateExpire.getTime() + lifeTime*60*1000);

    res.setHeader("Set-Cookie", name + "=" + value +";"
		                + "Expires=" + dateExpire + ";"
		                + "Path=/;"); 

		  
};
