exports.define = function(obj , property, value) {
    Object.defineProperty(obj , property, {
        value: value,
        enumerable: true
    });
};
exports.require = function(module) {
    var module = require(module);

    return module;
};
exports.isset = function(obj){
    return typeof obj != 'undefined';
};

var config = require(__dirname + "/../../config/vhost.json");
exports.paths = config[0].hosts.default.paths;