/*
 *Logs module
*/

var fs = require('fs');

exports.log = function(message,file){
    var now = new Date(),
	date = now.getFullYear() + '_' + now.getMonth() +'_'+ now.getDay()+ '_' 
              + now.getHours() +':'+ now.getMinutes() +':'+ now.getSeconds() +':'+
	    now.getMilliseconds();

    fs.appendFile(file,date+' '+ message+"\n", function(err) {
            if (err) throw err;
        });  

    console.log(date+' '+ message+"\n");
};
