/*
** Router module
**/ exports.get = function(routes, path) {

    var ctx = {
	name: 'error404',
	file: 'error404.html',
	ctrl: 'error404.js',
	type: 'html',
	query: {}
    };

    for (var i in routes) {
        if (typeof routes[i].format !== 'undefined') {
            var reg = new RegExp(routes[i].format,"i");
            var regs = path.match(reg); //différents parametres séparés par des / dans le pathname
	    
            if (regs) {
		if(typeof routes[i].query !== 'undefined') {
		    var j = 1;
		    for (var k in routes[i].query) {
			ctx.query[k] = regs[j];  //query{query_name:valeur_url}
			j++;
		    }
		}

                ctx.name = i;
		ctx.type = routes[i].type;
		//on va plutot remplacer dans route[i].url plutot ?????
                ctx.file = $.isset(ctx.query.file) ? routes[i].ressource.replace('[file]', ctx.query.file) : routes[i].ressource;
                ctx.ctrl = $.isset(ctx.query.ctrl) ? routes[i].controller.replace('[ctrl]',ctx.query.ctrl) : routes[i].controller;

		//on recopie les paramatres de routes[i] non présent dans ctx 
		for(var j in routes[i]){
		    if(typeof ctx[j] === 'undefined') {
			ctx[j] = routes[i][j];
		    }
		}
                break;
            }
        }

    }
    return ctx;
};
